import React, {Component} from 'react'
import Axios from 'axios';
import { response } from 'express';

const User = props => (
    <tr>
        <td>{props.user.username}</td>
    </tr>
)

export default class UserList extends Component {
    constructor(props){
        super(props)

        this.state = {
            users: []
        };
    }

    componentDidMount(){
        Axios.get("http://localhost:5000/users/")
        .then(res => {
            if (res.status === 200)
            {
                this.setState({
                    users: response.data
                })
            }
        })
    }

    listUsers() {
        return this.state.users.map(currentUser =>{
            return <User user={currentUser} gamePlayed={currentUser.preferredGame} />
        })
    }
    render(){
        return(
            <table>
                <tbody>
                    {this.listUsers()}
                </tbody>
            </table>
        )
    }

}
