import React, {Component} from 'react'
import axios from 'axios';


const User = props => (
    <tr>
        <td>{props.user.username}</td>
        <td>{props.user.profile.preferredGame}</td>
        <td>{props.user.profile.typeOfGamer}</td>
    </tr>
)

export default class UserList extends Component {
    constructor(props){
        super(props)

        this.state = {
            users: [],
            gameSearch: "",
        };
    }

    componentDidMount(){
        axios.get("http://localhost:5000/users/")
        .then(res => {
            if (res.status === 200)
            {
                this.setState({
                    users: res.data
                })
            }
        })
    }

    onChangeGameSearch = (e) => 
    {
        this.setState({
            gameSearch: e.target.value
        }, this.listUsers)
    }

    listUsers = () => {
        return this.state.users.map( currentUser =>{
     if ( this.state.gameSearch != "" && this.state.gameSearch === currentUser.preferredGame)
            {
                return <User user={currentUser} gamePlayed={currentUser.preferredGame} />
            }

        })
    }

    render(){
        return(
            <div>
                <input type="text"
                    value={this.state.gameSearch}
                    onChange={this.onChangeGameSearch}
                    />
                <table class="table">
                    <thead>
                        <tr>
                        <th scope="col">In Game Name</th>
                        <th scope="col">Game</th>
                        <th scope="col">Type of Gamer</th>
                        </tr>
                    </thead>
                    <tbody>
                    {this.listUsers()}
                    </tbody>
                    </table>
            </div>

        )
    }

}
