import React, {Component} from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'


export default class Navbar extends Component {
    constructor(props){
        super(props);
        this.onLogout = this.onLogout.bind(this)
    }

    onLogout(){
        axios.get("http://localhost:5000/users/logout")
        .then(res => {
            if (res.status === 200)
            {
                window.sessionStorage.clear()
                window.location ="/"
            }
        })
    }


    render(){
        const isAuth = window.sessionStorage.getItem("isAuthenticated");
        const username = window.sessionStorage.getItem("username");
        const id = window.sessionStorage.getItem("id")

        const profile = "/looter/" + id


        if(isAuth){
            return(
            <div className="mb-4">
                <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top" id="mainNav">
                    <div className="container">
                    <Link className="navbar-brand" to="/">Looking4Loot</Link>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarResponsive" aria-controls="navbarResponsive" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarResponsive">
                        <ul className="navbar-nav ml-auto">
                        <li className="nav-item">
                            <a className="nav-link" onClick={this.onLogout}>Logout</a>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to={profile}>{username}</Link>
                        </li>
                        </ul>
                    </div>
                    </div>
                </nav>

            </div>
        )} else{
            return(
                <div className="mb-4">
                    <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top" id="mainNav">
                        <div className="container">
                        <Link className="navbar-brand" to="/">Looking4Loot</Link>
                        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarResponsive" aria-controls="navbarResponsive" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className="collapse navbar-collapse" id="navbarResponsive">
                            <ul className="navbar-nav ml-auto">
                            <li className="nav-item">
                                <Link className="nav-link" to="/login">Login</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link" onClick>Sign Up</Link>
                             </li>
                            </ul>
                        </div>
                        </div>
                    </nav>

                </div>
            )
        }
    }

}
