import React, {Component} from 'react'
import axios from 'axios'


export default class UserProfile extends Component {

    constructor(props) {
        super(props);

        this.state={
            username: '',
            description: '',
            inGameName: '',
            gamesPlayed: '',
            typeOfGamer: ''
        }
    }

    onComponentMount(){
        axios.get("http://localhost:5000/users/" + this.props.match.params.id)
        .then(res => {
            if (res.status == 200)
            this.setState({
                username: res.username
            })
        })
    }

    render(){
        return(
            <div>

            </div>
        )
    }

}
