import React, {Component} from 'react'
import axios from 'axios';


const User = props => (
    <tr>
        <td>{props.user.username}</td>
        <td>{props.user.profile.preferredGame}</td>
        <td>{props.user.profile.typeOfGamer}</td>
    </tr>
)

export default class UserList extends Component {
    constructor(props){
        super(props)

        this.state = {
            users: []
        };
    }

    componentDidMount(){
        axios.get("http://localhost:5000/users/")
        .then(res => {
            if (res.status === 200)
            {
                this.setState({
                    users: res.data
                })
            }
        })
    }

    listUsers = () => {
        return this.state.users.map(currentUser =>{
            if( currentUser.lfg === true){
                return <User user={currentUser} gamePlayed={currentUser.preferredGame} />
            }

        })
    }

    render(){
        return(
            <table class="table">
                <thead>
                    <tr>
                    <th scope="col">In Game Name</th>
                    <th scope="col">Game</th>
                    <th scope="col">Type of Gamer</th>
                    </tr>
                </thead>
                <tbody>
                {this.listUsers()}
                </tbody>
                </table>

        )
    }

}
