import React, {Component} from 'react';
import { BrowserRouter as Router, Route } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "startbootstrap-scrolling-nav/css/scrolling-nav.css";
import axios from 'axios'

import $ from 'jquery';
import Popper from 'popper.js';
import jqueryEasing from 'jquery-easing'
import 'bootstrap/dist/js/bootstrap.bundle.min';

import Navbar           from "./components/navbar.component"
import LoginComponent   from "./components/login.component"
import landingComponent from "./components/landing.component"
import userList         from "./components/userList.component"
import userCreate       from "./components/userCreate.component"
import userProfile      from "./components/userProfile.component"
import userEdit         from "./components/userEdit.component" 




class App extends Component {
  constructor(props){
    super(props)
    this.loginHandler = this.loginHandler.bind(this)
    this.state =
    {
      isLoggedIn: false,
      user: null
    }
  }

  componentDidMount(){
    axios.get('http://localhost:5000/users')
    .then(res => {
      if(!!res.data.user){
        this.setState({
          isLoggedin: true,
          user: res.data.user
        })
      }
      else{
        this.setState({
            isLoggedIn: false,
            user: null
        })
      }
    })

  }


  loginHandler(arg){
    this.setState({
      isLoggedIn: arg
    })
  }


  render(){
  return (
    <div className="App">
      <Navbar isLoggedIn={this.state.isLoggedIn} user={this.state.user} />
      <br/>
      <Router>

        <Route path="/" exact component={landingComponent} />
        <Route path="/login" render={ props => <LoginComponent LoggedIn={this.loginHandler} />} />
        <Route path="/LookingForLooters" component={userList} />
        <Route path="/NewLooter" component={userCreate} />
        <Route path="/Looter/:id" component={userProfile} />
        <Route path="/EditLooter/:id" component={userEdit} />
      </Router>

    </div>
  );
}}

export default App;
